public class HuffmanServerDataTests
{
	/// <summary>
	/// Runs a test that parses bytes compressed using Huffman encoding, resulting in server data.
	/// </summary>
	/// <param name="data">The packet data that is to be parsed. The 2d array represents rows of bytes, each representing one single packet.</param>
	/// <param name="checks">A collection of checks that are used to validate the final parsed result.</param>
	[Theory]
	[MemberData(nameof(Data))]
	public void HuffmanEncodedBytesCanBeParsedIntoServerDataUsingBuilder(byte[][] data, Func<ServerResult, bool>[] checks)
	{
		Debug.Assert(data != null);
		Debug.Assert(checks != null);

		Packet[]? packets = null;
		var exception = Record.Exception(() =>
			packets = data.Select(x => new HuffmanPacket(x))
				.ToArray());

		Assert.Null(exception);
		Assert.NotNull(packets);

		var builder = new ServerResultBuilder(new IPEndPoint(IPAddress.Any, 0));
		exception = Record.Exception(() =>
		{
			foreach(var packet in packets)
			{
				_ = builder.Parse(packet);
			}
		});

		Assert.Null(exception);

		ServerResult? result = null;
		exception = Record.Exception(() =>
			result = builder.Build(ServerResultState.Success));

		Assert.Null(exception);
		Assert.NotNull(result);

		var index = 1;
		foreach(var check in checks)
		{
			Assert.True(check(result), $"Check {index} failed.");
			index++;
		}
	}

